(defn slogan-row [{:keys [n-row title desc]}]
  [:div.flex.align_c.p_mb
   [:span.fs_46.fw_700.c_yellow.mr_12 n-row]
   [:div
    [:p.c_dark.mb_2.fs_20.fw_500 title]
    [:p.c_dark desc]]])

