(defn feature-row [{:keys [icon title desc row-cls] :or {row-cls "align_s mb_14"}}]
  [:div.flex {:class row-cls}
   icon
   [:div
    [:h3.fs_18_mob.fw_500.mb_4.c_dark title]
    (when desc [:p.fw_100.c_dark desc])]])

(defn smooth-testimonials [{:keys [img-src p1 p2]}]
  [:div.s_ts_outer
   [:div.s_ts
    [:img.s_ts_bg_img {:alt "image" :src img-src}]
    [:div.s_ts_bg_img_mask]
    [:div.s_ts_txt_cont
     [:figure
      [:blockquote.s_ts_txt_cont
       [:p.mb_16 p1]
       [:p p2]]]]]])
